#include <iostream>#include <vector>#include <algorithm>#include <string>   #include <map>#include <set>#include <queue>#include <stack>#include <cmath>#include <iomanip>#include <bitset>#include <list>#include <deque>#include <forward_list>#include <unordered_map> using namespace std;typedef long long ll;  vector <bool> sieve (ll n){      vector <bool> is_prime (n+1,true);      is_prime[0]=false;      is_prime[1]=false;       for (ll i=2; i*i<=n; ++i){          if (is_prime[i]){             for (ll j=2*i; j<=n; j+=i){                 is_prime[j]=false;             }          }      }      return is_prime;} int main(){    ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);        ll n; cin >> n;    vector <ll> v(n);    vector <bool> is_prime = sieve(1000000);    for (ll i=0; i<n; i++){        cin>>v[i];    }            for (ll i=0; i<n; i++){        ll y = sqrtl(v[i]);        if (y*y==v[i]&&is_prime[y]){            cout << "YES" << "\n";        }         else {cout << "NO" << "\n";}              }             return 0;}